var moment = require('moment');

var mydate = new Date();

//console.log('Hello world');
console.log(`Fecha por defecto: ${mydate}`);
console.log(`Fecha en formato LL: ` + (moment().format('LL')));
console.log(`Fecha en formato LTS: ` + (moment().format('LTS')));
console.log(moment().endOf('day').fromNow());
console.log(moment().endOf('hour').fromNow());